package java_week_02;

public class week02_programing {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        
		
		//Print numbers from 0-10:
		//1. use for loop
		
		for(int i = 0; i <= 10; i ++){
			System.out.println(i);
			}
		
		
		//2. use while loop
		
		int x = 0;
		while ( x <= 10 ){
		System.out.println(x);
		x ++;
		}
		
		
		//3. use do while loop
		
		int i = 0;
		do {
		System.out.println(i);
		i ++;
		    }while(i <= 10);
		
		
		
		
		
	}

}
